import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { BehaviorSubject } from 'rxjs';
import{HttpHeaders} from '@angular/common/http';
import jwtDecode from 'jwt-decode';


@Injectable({
  providedIn: 'root'
})
export class AuthonticationService {
   isLogin = new BehaviorSubject(null);
   userName:string ="";

  constructor (private _HttpClien: HttpClient){
    let Token:any = localStorage.getItem('token'); 
    if (Token !== null) { 
    let userData: any = jwtDecode(Token);
    console.log(userData.title);
    this.userName = userData.title;
    }
     this.isLogin.next(Token);
  }

  logIn(userData:any):Observable<any>{
    let httpheaders = new HttpHeaders({
      'Accept': 'application/json',
     'Content-Type': 'application/json'
    })

     return  this._HttpClien.post('https://mashro3ylink.com/api/v1/clients/auth/login',{headers : httpheaders}, userData);
  }

}
