  // birthday: new FormControl(),
  // profile_background_color: new FormControl(),
  // active_profile_background: new FormControl(),
  // is_public: new FormControl(),

  });

 }

ngAfterViewInit(): void {
  this.buttontemp.nativeElement.click();
}


}
