import { Injectable } from '@angular/core';
import{HttpClient} from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ApiService {

  constructor(private _HttpClient:HttpClient) {}
  getAllLinks(){
    return this._HttpClient.get('https://mashro3ylink.com/api/v1/clients/links');
    }

    getOneLinkShow(){
      return this._HttpClient.get('https://mashro3ylink.com/api/v1/clients/links/:id');
    }

  AddLink(clientdata:any):Observable<any>{
    return this._HttpClient.post('https://mashro3ylink.com/api/v1/clients/links' , clientdata);

  }
UpdateLink(clientdata:any, id:number):Observable<any>{
  return this._HttpClient.post('https://mashro3ylink.com/api/v1/clients/links/:link_id'+id, clientdata);
}
DeleteLink(id:number){
  return this._HttpClient.delete('https://mashro3ylink.com/api/v1/clients/links/:link_id'+id);
}
}
