import { Component, OnInit } from '@angular/core';
import {FormGroup} from '@angular/forms';
import { FormControl} from '@angular/forms';
import { Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthonticationService } from '../authontication.service';
import jwtDecode from 'jwt-decode';


  @Component({
    selector: 'app-register',
    templateUrl: './register.component.html',
    styleUrls: ['./register.component.css']
  })
  export class RegisterComponent implements OnInit {


    registrationForm = new FormGroup({
      username: new FormControl("" , [Validators.required , Validators.minLength(3), Validators.maxLength(10)]),



      password: new FormControl("", [Validators.required , Validators.minLength(3)]),





    });

    apiresponse:any;


    constructor(private _AuthonticationService:AuthonticationService , private _router:Router) {}


     sendData(form:any){

      console.log(form.value);
      if (form.valid){

        this._AuthonticationService.logIn(form.value).subscribe({
          next:(response)=>{
          console.log(response.message);
          this.apiresponse = response.message;
          if(response.message == 'Token Created.'){
            localStorage.setItem('token', response.token);

            let userData: any = jwtDecode(response.token);
            console.log(userData.name);
            // this._AuthonticationService.userName = userData.name;

            //  this._AuthonticationService.isLogin.next(response.token);
            this._router.navigate(['/dashboard'])
          }
          },
          error:(err)=>{
            console.log(err);
          },
        });



      }else {

      }




    }




    ngOnInit(): void {
    }

  }
